model_y_5 <- survey::svyglm(
  formula = IPV_binary ~ race + informal_support_ordinal,
  design = dat_weights,
  family = "binomial"
)

med_5 <- mediation::mediate(
  model.m = model_m_5,
  model.y = model_y_5,
  sims = n_sims,
  treat = "race",
  mediator = "informal_support_ordinal",
